import { useState } from 'react';
import { useDispatch, useSelector } from "react-redux";
import { addTask, deleteTask } from "../store/tasksSlice";
import '../App.css';

export const NewTask = () => {

    const [task, setTask] = useState("");
    const [priority, setPriority] = useState("");
    const [date, setDate] = useState("");

    const dispatch = useDispatch();
    const taskList = useSelector((state) => state.tasks.list);

    const handleSubmit = () => {
        if (!task || !priority || !date) return;

        const newTask = {
            id: crypto.randomUUID(),
            task,
            priority,
            date
        };

        dispatch(addTask(newTask));

        setTask("");
        setPriority("");
        setDate("");
    };

    return (
        <div className="centered">
            <div className='pageHeading'>TODO</div>

            <div className="row1">
                <input type="text" value={task} onChange={(e)=>setTask(e.target.value)} className='inputBox' />
                <input type="text" value={priority} onChange={(e)=>setPriority(e.target.value)} className='inputBox'/>
                <input type="date" value={date} onChange={(e)=>setDate(e.target.value)} className='inputBox'/>
            </div>

            <div className='row1'>
                <button onClick={handleSubmit} className='addButton'>Add</button>
            </div>

            <table className='tableStyle'>
                <thead>
                    <tr>
                        <th>Task</th>
                        <th>Priority</th>
                        <th>Deadline</th>
                        <th>Delete</th>
                    </tr>
                </thead>

                <tbody>
                    {taskList.map((t) => (
                        <tr key={t.id}>
                            <td>{t.task}</td>
                            <td>{t.priority}</td>
                            <td>{t.date}</td>
                            <td>
                                <button onClick={() => dispatch(deleteTask(t.id))} className='deleteBtn'>‚ùå</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};
