import { useSelector } from "react-redux";

export const Dashboard = () => {
  const tasks = useSelector((state) => state.tasks.list);

  return (
    <div className="centered">
      <h2>Dashboard</h2>

      <table className="tableStyle">
        <thead>
          <tr>
            <th>Task</th>
            <th>Priority</th>
            <th>Deadline</th>
          </tr>
        </thead>

        <tbody>
          {tasks.map((t) => (
            <tr key={t.id}>
              <td>{t.task}</td>
              <td>{t.priority}</td>
              <td>{t.date}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};
